import useLanguage from '@/locale/useLanguage';
import CrudModule from '@/modules/CrudModule/CrudModule';
import FileForm from '@/forms/FileForm.jsx';
import { Flex, Image } from 'antd';
import React from 'react';

export default function Files() {
  const translate = useLanguage();
  const entity = 'files';
  const searchConfig = {
    displayLabels: ['name', 'published', 'subject'],
    searchFields: 'name,published,subject',
    outputValue: 'id',
  };

  const deleteModalLabels = ['name', 'published', 'subject'];

  const dataTableColumns = [
    {
      title: translate('Image'),
      dataIndex: 'image',
      render: (text, record, index) => {
        return (
          <Flex gap="small" align="start" vertical>
            <div style={{ padding: '15px' }}>
              <Image
                width={75}
                src={record.img}
                fallback="data:image/png;base64,"
              />
            </div>
          </Flex>
        );
      },
    },
    {
      title: translate('Name'),
      dataIndex: 'name',
    },
    {
      title: translate('Subject'),
      dataIndex: 'subject',
    },
    {
      title: translate('Published'),
      dataIndex: 'is_published',
      render: (isPublished, record, index) => {
        return isPublished ? 'True' : 'False';
      },
    },
  ];

  const readColumns = [
    {
      title: translate('Name'),
      dataIndex: 'name',
    },
    {
      title: translate('Subject'),
      dataIndex: 'subject',
    },
    {
      title: translate('Description'),
      dataIndex: 'description',
    },
    {
      title: translate('Number of pages'),
      dataIndex: 'number_of_page',
    },
    {
      title: translate('Number of minutes'),
      dataIndex: 'study_time',
    },
    {
      title: translate('Points'),
      dataIndex: 'points',
    },
    {
      title: translate('Published'),
      dataIndex: 'is_published',
    },
  ];

  const Labels = {
    PANEL_TITLE: translate('files'),
    DATATABLE_TITLE: translate('files_list'),
    ADD_NEW_ENTITY: translate('add_new_files'),
    ENTITY_NAME: translate('files'),
  };

  const configPage = {
    entity,
    ...Labels,
  };
  const config = {
    ...configPage,
    readColumns,
    dataTableColumns,
    searchConfig,
    deleteModalLabels,
  };
  return (
    <CrudModule
      createForm={<FileForm />}
      updateForm={<FileForm isUpdateForm={true} />}
      config={config}
      withUpload={true}
    />
  );
}
