import CrudModule from '@/modules/CrudModule/CrudModule';
import DynamicForm from '@/forms/DynamicForm';

import useLanguage from '@/locale/useLanguage';
import { Flex, Image, Switch } from 'antd';
import { CheckOutlined, CloseOutlined } from '@ant-design/icons';
import React from 'react';
import ReviewForm from '@/forms/ReviewForm.jsx';

export default function Reviews() {
  const translate = useLanguage();
  const entity = 'reviews';
  const searchConfig = {
    displayLabels: ['name', 'title', 'msg', 'rate'],
    searchFields: ['name', 'title', 'msg', 'rate'],
  };
  const deleteModalLabels = ['name', 'title', 'msg', 'rate'];

  const dataTableColumns = [
    {
      title: translate('Image'),
      dataIndex: 'image',
      render: (text, record, index) => {
        return (
          <Flex gap="small" align="start" vertical>
            <div style={{ padding: '15px' }}>
              <Image
                width={75}
                src={record.img}
                fallback="data:image/png;base64,"
              />
            </div>
          </Flex>
        );
      },
    },
    {
      title: translate('Name'),
      dataIndex: 'name',
    },
    {
      title: translate('Title'),
      dataIndex: 'title',
    },
    {
      title: translate('Message'),
      dataIndex: 'msg',
    },
    {
      title: translate('Rate'),
      dataIndex: 'rate',
    },
  ];
  const readColumns = [
    {
      title: translate('Name'),
      dataIndex: 'name',
    },
    {
      title: translate('Title'),
      dataIndex: 'title',
    },
    {
      title: translate('Message'),
      dataIndex: 'msg',
    },
    {
      title: translate('Rate'),
      dataIndex: 'rate',
    },
  ];

  const Labels = {
    PANEL_TITLE: translate('reviews'),
    DATATABLE_TITLE: translate('reviews_list'),
    ADD_NEW_ENTITY: translate('add_new_reviews'),
    ENTITY_NAME: translate('reviews'),
  };

  const configPage = {
    entity,
    ...Labels,
  };
  const config = {
    ...configPage,
    readColumns,
    dataTableColumns,
    searchConfig,
    deleteModalLabels,
  };
  return (
    <CrudModule
      createForm={<ReviewForm />}
      updateForm={<ReviewForm isUpdateForm={true} />}
      config={config}
      withUpload={true}
    />
  );
}
